import React, { useState } from "react";
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

class addPostComp extends React.Component{

    constructor(props){
        super(props);
        const [value, setValue] = useState('');
    }
    
    
    render(){
        return (<div class='addpost-form'>

                <form>
                <div class="form-group">
                    <label for="exampleFormControlInput1">Blog Title</label>
                    <input type="title" class="form-control" id="exampleFormControlInput1" placeholder="Here goes your title"/>
                </div>
                <div class="form-group">
                    <label for="exampleFormControlTextarea1">Content</label>
                    <ReactQuill theme="snow" value={value} onChange={setValue}/>
                </div>
                <button type="submit" class="btn btn-primary mt-4">Add post</button>
                </form>

        </div>);
    }
}

export default addPostComp;
