import React from 'react';
import axios from 'axios';

class postExpandComp extends React.Component {

    constructor(props) {
        super(props);
        this.myRef = React.createRef() 
        this.state = {
            post_title: '',
            post_content: '',
            post_image:''
        }
    }

    
    componentDidMount() {
        this.myRef.current.scrollTo(0, 0);

        axios.get('http://localhost:4000/posts/' + this.props.match.params.id)
            .then(response => {
                this.setState({ 
                    post_title: response.data.post_title,
                    post_content: response.data.post_content,
                    post_image: response.data.post_image
                })
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {

        return (
            <div className='container'>
                <div className='post-expand'>
                    <div ref={this.myRef} className='post-image' style={{ backgroundImage: 'url('+'http://localhost:4000/uploads/'+ this.state.post_image }}></div>
                    <div className='post-heading'>{this.state.post_title}</div>
                    <div className='post-content'><p>{this.state.post_content}</p></div>
                </div>
            </div>
        );
    }
}

export default postExpandComp;
