import React from 'react';
import axios from 'axios';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

class addPostComp extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            post_title: '',
            post_content: '',
            post_image: null
        }

        this.onChangePostContent = this.onChangePostContent.bind(this);
        this.onChangePostTitle = this.onChangePostTitle.bind(this);
        this.onChangePostImage = this.onChangePostImage.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

    }

    onChangePostTitle(e) {
        this.setState({ post_title: e.target.value })
    }

    onChangePostContent(e) {
        this.setState({ post_content: e.target.value })
    }

    onChangePostImage(e) {
        this.setState({ post_image: e.target.files[0] });
    }

    onSubmit(e) {
        e.preventDefault();

        let formData = new FormData();    //formdata object

        formData.append('post_title', this.state.post_title);   //append the values with key, value pair
        formData.append('post_content', this.state.post_content);
        formData.append('post_image',this.state.post_image);

        const config = {
            headers: { 'content-type': 'multipart/form-data' }
        }

        axios.post('http://localhost:4000/posts/addpost', formData)
            .then(res => console.log(res.data));

        this.setState({
            post_title: '',
            post_content: ''
        })
    }

    render() {
        return (<div class='addpost-form'>
            <span className='display-4 mb-3'>Post a new blog</span>
            <form className='mt-4' onSubmit={this.onSubmit} enctype="multipart/form-data">
                <div class="form-group">
                    <label for="addpost-title-label">Blog Title</label>
                    <input value={this.state.post_title} onChange={this.onChangePostTitle} name='post_title' type="title" class="form-control" id="addpost-title" placeholder="Title for your blog" />
                </div>
                <div class="form-group">
                    <label for="addpost-textarea-label">Content</label>
                    <ReactQuill theme="snow" value={this.state.post_content} onChange={this.onChangePostContent} />
                      </div>

                <div class="custom-file">
                    <input name='post_image' type="file" onChange={this.onChangePostImage} class="custom-file-input" id="validatedCustomFile" accept='.jpg' required />
                    <label class="custom-file-label" for="validatedCustomFile">Upload Blog Thumbnail</label>
                </div>

                <button type="submit" class="btn btn-primary mt-4">Add post</button>
            </form>

        </div>);
    }
}

export default addPostComp;
