import React from 'react';
import axios from 'axios';

class addPostComp extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            post_title: '',
            post_content: ''
        }
    }

    onChangePostTitle(e){
        this.setState({post_title: e.target.value})
    }

    onChangePostContent(e){
        this.setState({post_content: e.target.value})
    }

    render() {
        return (<div class='addpost-form'>
            <span className='display-4 mb-3'>Post a new blog</span>
            <form className='mt-4'>
                <div class="form-group">
                    <label for="addpost-title-label">Blog Title</label>
                    <input onChange = {this.onChangePostTitle} name='post_title' type="title" class="form-control" id="addpost-title" placeholder="Title for your blog" />
                </div>
                <div class="form-group">
                    <label for="addpost-textarea-label">Content</label>
                    <textarea onChange = {this.onChangePostContent} name='post_content' class="form-control" id="addpost-textarea" rows="3" rows='15' placeholder="Here goes the blog content"></textarea>
                </div>
                <button type="submit" class="btn btn-primary">Add post</button>
            </form>

        </div>);
    }
}

export default addPostComp;
