import React from 'react';
import Axios from 'axios';

class postExpandComp extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            post_id: props.post_id,
            post: '',
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/posts/' + this.state.post_id)
            .then(response => {
                this.setState({ post: response.data })
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {

        return (
            <div className='container'>
                <div className='post-expand'>
                    <div className='post-image' style={{ backgroundImage: "url('https://lh3.googleusercontent.com/S2nMbdcA3GsMQzJo6x5Tr0mt61czPoWqjr-hXid66TgAxQIRlMv4-tSCnyNe4qibwIh7')" }}></div>
                    <div className='post-heading'>{this.state.post.post_title}</div>
                    <div className='post-content'><p>{this.state.post.post_content}</p></div>
                </div>
            </div>
        );
    }
}

export default postExpandComp;
