import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import addPostComp from './components/addpost.component'
import feedComp from './components/feed.component'
import postExpandComp from './components/postexpand.component'

class App extends React.Component {
  render() {
    return (
      <Router>
        <div className='navclass container-fluid'>
          <div className='row'>
            <div className='col-2'>
            </div>
            <div className='col-6'>
              <Link to='/' className='mr-4 navbar-title'>BlogRack</Link>
            </div>
            <div className='col-2'>
              <Link to='/' className='mr-4 navlinks'>Feed</Link>
              <Link to='/postexpand' className='mr-4 navlinks'>Read</Link>
              <Link to='/addpost' className='mr-4 navlinks'>Add Post</Link>
            </div>
            <div className='col'>

            </div>
          </div>
        </div>

        <div className='container content-box'>
          <Route path="/" exact component={feedComp} />
          <Route path="/addpost" exact component={addPostComp} />
          <Route path="/postexpand" exact component={postExpandComp} />
        </div>

      <div className='footer-box'>
        Developed with ❤️ by Aryan
      </div>
      </Router>
    );
  }

}

export default App;
